     1â†’'use client';
     2â†’
     3â†’/**
     4â†’ * Block Puzzle Game - Konva.js Implementation (Responsive)
     5â†’ *
     6â†’ * Production-ready game component with fully responsive design
     7â†’ * Features:
     8â†’ * - Native drag and drop with Konva
     9â†’ * - Grid-based placement with validation
    10â†’ * - Line clearing (rows and columns)
    11â†’ * - Score tracking with combo multiplier
    12â†’ * - Block respawning
    13â†’ * - Debug mode with visual feedback
    14â†’ * - Level configuration support
    15â†’ * - Fully responsive (adapts to any screen size)
    16â†’ */
    17â†’
    18â†’import { useEffect, useRef, useState, useCallback } from 'react';
    19â†’import { Stage, Layer, Rect, Group, Text } from 'react-konva';
    20â†’import Konva from 'konva';
    21â†’import type { LevelConfig } from '@/lib/game/levels/levelService';
    22â†’import { musicManager } from '@/lib/audio/MusicManager';
    23â†’import { soundEffects } from '@/lib/audio/SoundEffects';
    24â†’import { haptics } from '@/lib/utils/haptics';
    25â†’import { ComboFeedback } from './ComboFeedback';
    26â†’import { Tutorial } from './Tutorial';
    27â†’import { AudioControls } from './AudioControls';
    28â†’
    29â†’// Game constants
    30â†’const GRID_SIZE = 8;
    31â†’
    32â†’// Responsive sizing constraints
    33â†’const MIN_CELL_SIZE = 30; // Minimum for mobile
    34â†’const MAX_CELL_SIZE = 42; // Maximum for large screens (smaller for better fit)
    35â†’const GRID_OFFSET_RATIO = 0.08; // 8% of stage width/height for offsets
    36â†’const MIN_STAGE_WIDTH = 320; // Minimum viewport support
    37â†’
    38â†’interface BlockData {
    39â†’  id: string;
    40â†’  shape: number[][];
    41â†’  color: string;
    42â†’  x: number;
    43â†’  y: number;
    44â†’  originalX: number;
    45â†’  originalY: number;
    46â†’}
    47â†’
    48â†’export interface KonvaGameProps {
    49â†’  level: LevelConfig;
    50â†’  onScoreUpdate?: (score: number) => void;
    51â†’  onGameOver?: () => void;
    52â†’  onLevelComplete?: () => void;
    53â†’}
    54â†’
    55â†’/**
    56â†’ * Calculate responsive dimensions based on container width
    57â†’ */
    58â†’function calculateDimensions(containerWidth: number) {
    59â†’  const safeWidth = Math.max(containerWidth, MIN_STAGE_WIDTH);
    60â†’
    61â†’  // Calculate cell size based on available width
    62â†’  // Reserve space for offsets on both sides
    63â†’  const availableWidth = safeWidth - (safeWidth * GRID_OFFSET_RATIO * 2);
    64â†’  let cellSize = Math.floor(availableWidth / GRID_SIZE);
    65â†’
    66â†’  // Clamp cell size to min/max
    67â†’  cellSize = Math.max(MIN_CELL_SIZE, Math.min(MAX_CELL_SIZE, cellSize));
    68â†’
    69â†’  // Calculate actual dimensions
    70â†’  const gridOffsetX = Math.floor(safeWidth * GRID_OFFSET_RATIO);
    71â†’  const gridOffsetY = gridOffsetX;
    72â†’  const gridWidth = cellSize * GRID_SIZE;
    73â†’  const stageWidth = gridWidth + (gridOffsetX * 2);
    74â†’  const spawnY = gridOffsetY + gridWidth + Math.floor(cellSize * 1.5);
    75â†’  // Increase space for blocks - assume max 3x3 block size + comfortable spacing
    76â†’  const maxBlockHeight = cellSize * 3; // Max block is 3 cells tall
    77â†’  const stageHeight = spawnY + maxBlockHeight + (cellSize * 1.5); // Extra space for comfortable touch
    78â†’
    79â†’  return {
    80â†’    cellSize,
    81â†’    gridOffsetX,
    82â†’    gridOffsetY,
    83â†’    stageWidth,
    84â†’    stageHeight,
    85â†’    spawnY,
    86â†’  };
    87â†’}
    88â†’
    89â†’export function KonvaGame({ level, onScoreUpdate, onGameOver, onLevelComplete }: KonvaGameProps) {
    90â†’  // Responsive dimensions state
    91â†’  const [dimensions, setDimensions] = useState(() => calculateDimensions(800)); // Default for SSR
    92â†’
    93â†’  // Game state
    94â†’  const [grid, setGrid] = useState<number[][]>(() =>
    95â†’    Array(GRID_SIZE).fill(0).map(() => Array(GRID_SIZE).fill(0))
    96â†’  );
    97â†’  const [blocks, setBlocks] = useState<BlockData[]>([]);
    98â†’  const [score, setScore] = useState(0);
    99â†’  const [comboCount, setComboCount] = useState(0);
   100â†’  const [activeBlocks, setActiveBlocks] = useState(0);
   101â†’  const [isLoading, setIsLoading] = useState(true);
   102â†’  const [isGameOver, setIsGameOver] = useState(false);
   103â†’  const [isLevelWon, setIsLevelWon] = useState(false);
   104â†’  const [dragPreview, setDragPreview] = useState<{
   105â†’    row: number;
   106â†’    col: number;
   107â†’    shape: number[][];
   108â†’    isValid: boolean;
   109â†’  } | null>(null);
   110â†’
   111â†’  // New engagement features state
   112â†’  const [showTutorial, setShowTutorial] = useState(true);
   113â†’  const [showComboFeedback, setShowComboFeedback] = useState(false);
   114â†’  const [lastClearedLines, setLastClearedLines] = useState(0);
   115â†’  const [audioInitialized, setAudioInitialized] = useState(false);
   116â†’
   117â†’  const stageRef = useRef<Konva.Stage>(null);
   118â†’  const containerRef = useRef<HTMLDivElement>(null);
   119â†’  const levelBlocksRef = useRef<typeof level.blocks>([]);
   120â†’  const initializedRef = useRef(false);
   121â†’  const onScoreUpdateRef = useRef(onScoreUpdate);
   122â†’
   123â†’  // Keep score callback ref up to date
   124â†’  useEffect(() => {
   125â†’    onScoreUpdateRef.current = onScoreUpdate;
   126â†’  }, [onScoreUpdate]);
   127â†’
   128â†’  // Initialize audio systems on first user interaction
   129â†’  useEffect(() => {
   130â†’    if (!audioInitialized) {
   131â†’      const initAudio = () => {
   132â†’        musicManager.initialize();
   133â†’        soundEffects.initialize();
   134â†’        setAudioInitialized(true);
   135â†’
   136â†’        // Start music (user can mute via controls)
   137â†’        musicManager.play();
   138â†’      };
   139â†’
   140â†’      // CRITICAL FIX: Use { once: true } to auto-remove listeners after first trigger
   141â†’      // This prevents memory leaks and ensures cleanup even if component unmounts
   142â†’      document.addEventListener('click', initAudio, { once: true });
   143â†’      document.addEventListener('touchstart', initAudio, { once: true });
   144â†’
   145â†’      return () => {
   146â†’        // Cleanup in case component unmounts before user interaction
   147â†’        document.removeEventListener('click', initAudio);
   148â†’        document.removeEventListener('touchstart', initAudio);
   149â†’      };
   150â†’    }
   151â†’    return undefined;
   152â†’  }, [audioInitialized]);
   153â†’
   154â†’  // Handle responsive resize
   155â†’  useEffect(() => {
   156â†’    const updateDimensions = () => {
   157â†’      // Detect mobile/tablet vs desktop
   158â†’      const isMobile = window.innerWidth < 768; // Tailwind's 'md' breakpoint
   159â†’      const viewportWidth = window.innerWidth;
   160â†’
   161â†’      // Mobile: use FULL viewport width minus minimal margins (maximize grid)
   162â†’      // Desktop: use container width, capped at 600px
   163â†’      const maxGameWidth = isMobile
   164â†’        ? Math.min(viewportWidth * 0.98, 600) // 98% for mobile = nearly fullscreen
   165â†’        : Math.min(viewportWidth * 0.7, 600);
   166â†’
   167â†’      const availableWidth = containerRef.current?.offsetWidth || maxGameWidth;
   168â†’      const calculatedWidth = Math.min(availableWidth, maxGameWidth);
   169â†’
   170â†’      setDimensions(calculateDimensions(calculatedWidth));
   171â†’    };
   172â†’
   173â†’    // Set initial dimensions after a short delay to ensure DOM is ready
   174â†’    setTimeout(updateDimensions, 100);
   175â†’
   176â†’    // Add resize listener with debounce
   177â†’    let resizeTimer: NodeJS.Timeout;
   178â†’    const debouncedResize = () => {
   179â†’      clearTimeout(resizeTimer);
   180â†’      resizeTimer = setTimeout(updateDimensions, 150);
   181â†’    };
   182â†’
   183â†’    window.addEventListener('resize', debouncedResize);
   184â†’    return () => {
   185â†’      window.removeEventListener('resize', debouncedResize);
   186â†’      clearTimeout(resizeTimer);
   187â†’    };
   188â†’  }, []);
   189â†’
   190â†’  // Initialize level blocks ONCE
   191â†’  useEffect(() => {
   192â†’    if (!initializedRef.current && level.blocks && level.blocks.length > 0) {
   193â†’      initializedRef.current = true;
   194â†’      levelBlocksRef.current = level.blocks;
   195â†’      spawnBlocks();
   196â†’      setIsLoading(false);
   197â†’    }
   198â†’    // eslint-disable-next-line react-hooks/exhaustive-deps
   199â†’  }, [level.levelNumber]); // Only re-initialize when level number changes
   200â†’
   201â†’  // Update score callback (using ref to prevent infinite loop)
   202â†’  useEffect(() => {
   203â†’    if (onScoreUpdateRef.current) {
   204â†’      onScoreUpdateRef.current(score);
   205â†’    }
   206â†’  }, [score]);
   207â†’
   208â†’  // Check for level completion when score changes
   209â†’  useEffect(() => {
   210â†’    if (score >= level.targetScore && !isLevelWon && !isGameOver) {
   211â†’      setIsLevelWon(true);
   212â†’
   213â†’      // Play victory feedback
   214â†’      soundEffects.play('levelWin');
   215â†’      haptics.levelComplete();
   216â†’
   217â†’      if (onLevelComplete) {
   218â†’        onLevelComplete();
   219â†’      }
   220â†’    }
   221â†’  }, [score, level.targetScore, isLevelWon, isGameOver, onLevelComplete]);
   222â†’
   223â†’  /**
   224â†’   * Spawn 3 random blocks from level configuration
   225â†’   * Improved positioning to prevent overlap
   226â†’   */
   227â†’  const spawnBlocks = useCallback(() => {
   228â†’    if (!levelBlocksRef.current || levelBlocksRef.current.length === 0) {
   229â†’      return;
   230â†’    }
   231â†’
   232â†’    const newBlocks: BlockData[] = [];
   233â†’    const availableBlocks = levelBlocksRef.current;
   234â†’
   235â†’    // First, select the blocks and calculate their actual widths
   236â†’    const selectedBlocks = [];
   237â†’    for (let i = 0; i < 3; i++) {
   238â†’      const blockConfig = availableBlocks[Math.floor(Math.random() * availableBlocks.length)];
   239â†’      const blockWidth = blockConfig.shape[0].length * dimensions.cellSize;
   240â†’      selectedBlocks.push({
   241â†’        config: blockConfig,
   242â†’        width: blockWidth,
   243â†’      });
   244â†’    }
   245â†’
   246â†’    // Calculate total width needed
   247â†’    const totalBlocksWidth = selectedBlocks.reduce((sum, block) => sum + block.width, 0);
   248â†’    const gap = dimensions.cellSize * 0.5; // Half cell gap between blocks
   249â†’    const totalGapsWidth = gap * 4; // gaps: [start] block [gap] block [gap] block [end]
   250â†’    const totalNeededWidth = totalBlocksWidth + totalGapsWidth;
   251â†’    const availableWidth = dimensions.stageWidth - (dimensions.gridOffsetX * 2);
   252â†’
   253â†’    // Calculate starting X position to center all blocks together
   254â†’    let currentX = dimensions.gridOffsetX + (availableWidth - totalNeededWidth) / 2 + gap;
   255â†’
   256â†’    // Position blocks side by side with proper spacing
   257â†’    for (let i = 0; i < selectedBlocks.length; i++) {
   258â†’      const { config, width } = selectedBlocks[i];
   259â†’
   260â†’      newBlocks.push({
   261â†’        id: `block-${Date.now()}-${i}`,
   262â†’        shape: config.shape,
   263â†’        color: config.color,
   264â†’        x: currentX,
   265â†’        y: dimensions.spawnY,
   266â†’        originalX: currentX,
   267â†’        originalY: dimensions.spawnY,
   268â†’      });
   269â†’
   270â†’      // Move to next position (current block width + gap)
   271â†’      currentX += width + gap;
   272â†’    }
   273â†’
   274â†’    // Set blocks with staggered animation
   275â†’    setBlocks([]);
   276â†’    newBlocks.forEach((block, index) => {
   277â†’      setTimeout(() => {
   278â†’        setBlocks(prev => [...prev, block]);
   279â†’        if (index === newBlocks.length - 1) {
   280â†’          setActiveBlocks(3);
   281â†’        }
   282â†’      }, index * 150); // 150ms delay between each block
   283â†’    });
   284â†’  }, [dimensions]);
   285â†’
   286â†’
   287â†’  /**
   288â†’   * Convert world coordinates to grid position
   289â†’   * Uses floor for accurate top-left corner alignment
   290â†’   */
   291â†’  const worldToGrid = useCallback((worldX: number, worldY: number) => {
   292â†’    const gridX = worldX - dimensions.gridOffsetX;
   293â†’    const gridY = worldY - dimensions.gridOffsetY;
   294â†’
   295â†’    // Check if position is within grid bounds (with small tolerance)
   296â†’    if (gridX < 0 || gridY < 0 ||
   297â†’        gridX >= GRID_SIZE * dimensions.cellSize ||
   298â†’        gridY >= GRID_SIZE * dimensions.cellSize) {
   299â†’      return null;
   300â†’    }
   301â†’
   302â†’    // Use floor to detect which cell the top-left corner is in
   303â†’    // This gives accurate placement matching the visual position
   304â†’    const col = Math.floor(gridX / dimensions.cellSize);
   305â†’    const row = Math.floor(gridY / dimensions.cellSize);
   306â†’
   307â†’    // Ensure we're within bounds
   308â†’    if (row < 0 || col < 0 || row >= GRID_SIZE || col >= GRID_SIZE) {
   309â†’      return null;
   310â†’    }
   311â†’
   312â†’    return { row, col };
   313â†’  }, [dimensions]);
   314â†’
   315â†’  /**
   316â†’   * Check if a block can be placed at the given position
   317â†’   */
   318â†’  const canPlaceBlock = useCallback((row: number, col: number, shape: number[][]) => {
   319â†’    if (row < 0 || col < 0) return false;
   320â†’    if (row + shape.length > GRID_SIZE) return false;
   321â†’    if (col + shape[0].length > GRID_SIZE) return false;
   322â†’
   323â†’    for (let r = 0; r < shape.length; r++) {
   324â†’      for (let c = 0; c < shape[r].length; c++) {
   325â†’        if (shape[r][c] === 1 && grid[row + r][col + c] !== 0) {
   326â†’          return false;
   327â†’        }
   328â†’      }
   329â†’    }
   330â†’
   331â†’    return true;
   332â†’  }, [grid]);
   333â†’
   334â†’  /**
   335â†’   * Check if any of the current blocks can be placed anywhere on the grid
   336â†’   * Returns true if at least one move is possible, false if game over
   337â†’   */
   338â†’  const checkMovesAvailable = useCallback((currentBlocks: BlockData[]) => {
   339â†’    // Try each block
   340â†’    for (const block of currentBlocks) {
   341â†’      // Try each position on the grid
   342â†’      for (let row = 0; row < GRID_SIZE; row++) {
   343â†’        for (let col = 0; col < GRID_SIZE; col++) {
   344â†’          if (canPlaceBlock(row, col, block.shape)) {
   345â†’            // Found at least one valid move
   346â†’            return true;
   347â†’          }
   348â†’        }
   349â†’      }
   350â†’    }
   351â†’    // No valid moves found - game over
   352â†’    return false;
   353â†’  }, [canPlaceBlock]);
   354â†’
   355â†’  // Check for game over after blocks change
   356â†’  useEffect(() => {
   357â†’    if (blocks.length > 0 && !isLoading && !isGameOver) {
   358â†’      // Small delay to ensure grid state is updated
   359â†’      const timer = setTimeout(() => {
   360â†’        const movesAvailable = checkMovesAvailable(blocks);
   361â†’        if (!movesAvailable) {
   362â†’          setIsGameOver(true);
   363â†’
   364â†’          // Play game over feedback
   365â†’          soundEffects.play('gameOver');
   366â†’          haptics.gameOver();
   367â†’
   368â†’          if (onGameOver) {
   369â†’            onGameOver();
   370â†’          }
   371â†’        }
   372â†’      }, 100);
   373â†’      return () => clearTimeout(timer);
   374â†’    }
   375â†’    return undefined;
   376â†’  }, [blocks, isLoading, isGameOver, checkMovesAvailable, onGameOver]);
   377â†’
   378â†’  /**
   379â†’   * Check and clear completed lines (rows and columns)
   380â†’   */
   381â†’  const checkAndClearLines = useCallback((currentGrid: number[][]) => {
   382â†’    const newGrid = currentGrid.map(row => [...row]);
   383â†’    let linesCleared = 0;
   384â†’    const clearedRows: number[] = [];
   385â†’    const clearedCols: number[] = [];
   386â†’
   387â†’    // Check rows
   388â†’    for (let row = 0; row < GRID_SIZE; row++) {
   389â†’      if (newGrid[row].every(cell => cell !== 0)) {
   390â†’        newGrid[row].fill(0);
   391â†’        linesCleared++;
   392â†’        clearedRows.push(row);
   393â†’      }
   394â†’    }
   395â†’
   396â†’    // Check columns
   397â†’    for (let col = 0; col < GRID_SIZE; col++) {
   398â†’      const columnFilled = newGrid.every(row => row[col] !== 0);
   399â†’      if (columnFilled) {
   400â†’        for (let row = 0; row < GRID_SIZE; row++) {
   401â†’          newGrid[row][col] = 0;
   402â†’        }
   403â†’        linesCleared++;
   404â†’        clearedCols.push(col);
   405â†’      }
   406â†’    }
   407â†’
   408â†’    return { linesCleared, clearedRows, clearedCols, newGrid };
   409â†’  }, []);
   410â†’
   411â†’  /**
   412â†’   * Place a block on the grid
   413â†’   */
   414â†’  const placeBlock = useCallback((blockId: string, row: number, col: number, shape: number[][], color: string) => {
   415â†’    const newGrid = grid.map(row => [...row]);
   416â†’    const colorValue = parseInt(color.replace('#', ''), 16);
   417â†’    let cellsPlaced = 0;
   418â†’
   419â†’    // Place block cells on grid
   420â†’    for (let r = 0; r < shape.length; r++) {
   421â†’      for (let c = 0; c < shape[r].length; c++) {
   422â†’        if (shape[r][c] === 1) {
   423â†’          newGrid[row + r][col + c] = colorValue;
   424â†’          cellsPlaced++;
   425â†’        }
   426â†’      }
   427â†’    }
   428â†’
   429â†’    setGrid(newGrid);
   430â†’
   431â†’    // Play block placement feedback
   432â†’    soundEffects.play('blockPlace');
   433â†’    haptics.blockPlace();
   434â†’
   435â†’    // Remove the placed block
   436â†’    setBlocks(prev => prev.filter(b => b.id !== blockId));
   437â†’    const newActiveCount = activeBlocks - 1;
   438â†’    setActiveBlocks(newActiveCount);
   439â†’
   440â†’    // Add score for placed cells
   441â†’    const cellScore = cellsPlaced * 10;
   442â†’    setScore(prev => prev + cellScore);
   443â†’
   444â†’    // Check for completed lines
   445â†’    setTimeout(() => {
   446â†’      const { linesCleared, newGrid: clearedGrid } = checkAndClearLines(newGrid);
   447â†’
   448â†’      if (linesCleared > 0) {
   449â†’        setGrid(clearedGrid);
   450â†’
   451â†’        // Play clear feedback
   452â†’        if (linesCleared === 1) {
   453â†’          soundEffects.play('lineClear');
   454â†’        } else {
   455â†’          soundEffects.playMultiClear(linesCleared);
   456â†’        }
   457â†’        haptics.lineClear(linesCleared);
   458â†’
   459â†’        // Update combo and score
   460â†’        setComboCount(prev => {
   461â†’          const newCombo = prev + 1;
   462â†’          const lineScore = linesCleared * 100 * newCombo;
   463â†’          setScore(prevScore => prevScore + lineScore);
   464â†’
   465â†’          // Play combo feedback
   466â†’          if (newCombo > 1) {
   467â†’            soundEffects.playCombo(newCombo);
   468â†’            haptics.comboAchieved(newCombo);
   469â†’          }
   470â†’
   471â†’          // Show visual feedback
   472â†’          setLastClearedLines(linesCleared);
   473â†’          setShowComboFeedback(true);
   474â†’
   475â†’          return newCombo;
   476â†’        });
   477â†’      } else {
   478â†’        // Reset combo if no lines cleared
   479â†’        setComboCount(0);
   480â†’      }
   481â†’
   482â†’      // Spawn new blocks if all placed
   483â†’      if (newActiveCount === 0) {
   484â†’        setTimeout(spawnBlocks, 500);
   485â†’      }
   486â†’    }, 100);
   487â†’  }, [grid, activeBlocks, spawnBlocks, checkAndClearLines]);
   488â†’
   489â†’  /**
   490â†’   * Find the nearest valid position for a block near the grid
   491â†’   * Returns null if no valid position within snap radius
   492â†’   */
   493â†’  const findNearestValidPosition = useCallback((x: number, y: number, shape: number[][]) => {
   494â†’    // Define snap radius (1.5 cells)
   495â†’    const snapRadius = dimensions.cellSize * 1.5;
   496â†’
   497â†’    // Try positions in a spiral pattern around the drop point
   498â†’    const centerRow = Math.round((y - dimensions.gridOffsetY) / dimensions.cellSize);
   499â†’    const centerCol = Math.round((x - dimensions.gridOffsetX) / dimensions.cellSize);
   500â†’
   501â†’    let bestPosition: { row: number; col: number; distance: number } | null = null;
   502â†’
   503â†’    // Search in expanding radius
   504â†’    for (let radius = 0; radius <= 2; radius++) {
   505â†’      for (let row = centerRow - radius; row <= centerRow + radius; row++) {
   506â†’        for (let col = centerCol - radius; col <= centerCol + radius; col++) {
   507â†’          // Check bounds
   508â†’          if (row < 0 || col < 0 || row >= GRID_SIZE || col >= GRID_SIZE) continue;
   509â†’
   510â†’          // Check if this position is valid
   511â†’          if (canPlaceBlock(row, col, shape)) {
   512â†’            // Calculate actual pixel position
   513â†’            const posX = dimensions.gridOffsetX + col * dimensions.cellSize;
   514â†’            const posY = dimensions.gridOffsetY + row * dimensions.cellSize;
   515â†’
   516â†’            // Calculate distance from drop point
   517â†’            const distance = Math.sqrt(Math.pow(x - posX, 2) + Math.pow(y - posY, 2));
   518â†’
   519â†’            // Only consider if within snap radius
   520â†’            if (distance <= snapRadius) {
   521â†’              if (!bestPosition || distance < bestPosition.distance) {
   522â†’                bestPosition = { row, col, distance };
   523â†’              }
   524â†’            }
   525â†’          }
   526â†’        }
   527â†’      }
   528â†’    }
   529â†’
   530â†’    return bestPosition ? { row: bestPosition.row, col: bestPosition.col } : null;
   531â†’  }, [dimensions, canPlaceBlock]);
   532â†’
   533â†’  /**
   534â†’   * Handle drag move for a block - show preview of placement
   535â†’   */
   536â†’  const handleDragMove = useCallback((blockId: string) => (e: Konva.KonvaEventObject<DragEvent>) => {
   537â†’    const block = blocks.find(b => b.id === blockId);
   538â†’    if (!block) return;
   539â†’
   540â†’    const shape = e.target;
   541â†’    const x = shape.x();
   542â†’    const y = shape.y();
   543â†’
   544â†’    // Get grid position
   545â†’    const gridPos = worldToGrid(x, y);
   546â†’
   547â†’    if (gridPos) {
   548â†’      const { row, col } = gridPos;
   549â†’      const isValid = canPlaceBlock(row, col, block.shape);
   550â†’      setDragPreview({ row, col, shape: block.shape, isValid });
   551â†’    } else {
   552â†’      setDragPreview(null);
   553â†’    }
   554â†’  }, [blocks, worldToGrid, canPlaceBlock]);
   555â†’
   556â†’  /**
   557â†’   * Handle drag end for a block
   558â†’   * Improved with magnetic snapping to nearest valid position
   559â†’   */
   560â†’  const handleDragEnd = useCallback((blockId: string) => (e: Konva.KonvaEventObject<DragEvent>) => {
   561â†’    const block = blocks.find(b => b.id === blockId);
   562â†’    if (!block) return;
   563â†’
   564â†’    // Clear drag preview
   565â†’    setDragPreview(null);
   566â†’
   567â†’    const shape = e.target;
   568â†’    const x = shape.x();
   569â†’    const y = shape.y();
   570â†’
   571â†’    // First, try exact grid position
   572â†’    const gridPos = worldToGrid(x, y);
   573â†’    let targetRow: number;
   574â†’    let targetCol: number;
   575â†’
   576â†’    if (gridPos && canPlaceBlock(gridPos.row, gridPos.col, block.shape)) {
   577â†’      // Exact position is valid
   578â†’      targetRow = gridPos.row;
   579â†’      targetCol = gridPos.col;
   580â†’    } else {
   581â†’      // Try to find nearest valid position (magnetic snap)
   582â†’      const nearestPos = findNearestValidPosition(x, y, block.shape);
   583â†’
   584â†’      if (nearestPos) {
   585â†’        targetRow = nearestPos.row;
   586â†’        targetCol = nearestPos.col;
   587â†’      } else {
   588â†’        // Return to original position with animation
   589â†’        shape.to({
   590â†’          x: block.originalX,
   591â†’          y: block.originalY,
   592â†’          duration: 0.3,
   593â†’        });
   594â†’        return;
   595â†’      }
   596â†’    }
   597â†’
   598â†’    // Snap to target grid position visually before placing
   599â†’    const snapX = dimensions.gridOffsetX + targetCol * dimensions.cellSize;
   600â†’    const snapY = dimensions.gridOffsetY + targetRow * dimensions.cellSize;
   601â†’
   602â†’    // Animate to exact grid position
   603â†’    shape.to({
   604â†’      x: snapX,
   605â†’      y: snapY,
   606â†’      duration: 0.15,
   607â†’      onFinish: () => {
   608â†’        placeBlock(blockId, targetRow, targetCol, block.shape, block.color);
   609â†’      }
   610â†’    });
   611â†’  }, [blocks, worldToGrid, canPlaceBlock, findNearestValidPosition, placeBlock, dimensions]);
   612â†’
   613â†’  if (isLoading) {
