   717â†’                          width={dimensions.cellSize - 6}
   718â†’                          height={dimensions.cellSize - 6}
   719â†’                          fill={dragPreview.isValid ? '#22c55e' : '#ef4444'}
   720â†’                          opacity={0.4}
   721â†’                          cornerRadius={6}
   722â†’                          listening={false}
