import type { Metadata, Viewport } from 'next';
import './globals.css';

export const metadata: Metadata = {
  title: 'Block Puzzle Hero',
  description: 'A modern block puzzle game with tiered monetization',
};

export const viewport: Viewport = {
  width: 'device-width',
  initialScale: 1,
  maximumScale: 1,
  userScalable: false, // Prevent zoom for app-like experience
  themeColor: '#0ea5e9',
  viewportFit: 'cover', // Fullscreen on notched devices
};

export default function RootLayout({
  children,
}: {
  children: React.ReactNode;
}) {
  const basePath = process.env.NODE_ENV === 'production' ? '/block-puzzle-hero' : '';

  return (
    <html lang="en">
      <head>
        <link rel="manifest" href={`${basePath}/manifest.json`} />
      </head>
      <body className="font-sans antialiased">
        <main className="min-h-screen bg-gray-50">{children}</main>
      </body>
    </html>
  );
}

import { GameClient } from './GameClient';

// Mock level data - in production, fetch from API
const mockLevelData = {
  levelNumber: 1,
  name: 'Getting Started',
  description: 'Learn the basics of block placement',
  difficulty: 1,
  tier: 'FREE' as const,
  gridConfig: { rows: 8, cols: 8 },
  targetScore: 500,
  timeLimit: null,
  blocks: [
    { id: 'I', shape: [[1, 1, 1, 1]], color: '#3B82F6', frequency: 35 },
    { id: 'O', shape: [[1, 1], [1, 1]], color: '#10B981', frequency: 35 },
    { id: 'T', shape: [[1, 1, 1], [0, 1, 0]], color: '#F59E0B', frequency: 30 },
  ],
};

// Generate static params for static export (FREE tier levels)
export function generateStaticParams() {
  return [
    { level: '1' },
    { level: '2' },
    { level: '3' },
  ];
}

export default async function PlayLevelPage({ params }: { params: Promise<{ level: string }> }) {
  const resolvedParams = await params;

  return <GameClient level={resolvedParams.level} levelData={mockLevelData} />;
}

