     1→import React, { useState, useEffect } from 'react';
     2→import { TrendingUp, TrendingDown, Activity, AlertCircle, CheckCircle, Clock, Target, DollarSign, BarChart3, Zap, Filter, RefreshCw } from 'lucide-react';
     3→
     4→const ElliottWaveLiveSignals = () => {
     5→  const [activeSignals, setActiveSignals] = useState([]);
     6→  const [scanning, setScanning] = useState(false);
     7→  const [selectedSignal, setSelectedSignal] = useState(null);
     8→  const [autoRefresh, setAutoRefresh] = useState(true);
     9→  const [lastUpdate, setLastUpdate] = useState(new Date());
    10→  const [filterCategory, setFilterCategory] = useState('all');
    11→  const [filterScore, setFilterScore] = useState(75);
    12→
    13→  // Expanded Asset Universe
    14→  const assetCategories = {
    15→    'Forex Majors': ['EUR/USD', 'GBP/USD', 'USD/JPY', 'AUD/USD', 'USD/CHF', 'USD/CAD', 'NZD/USD'],
    16→    'Commodities': ['Gold', 'Silver', 'Oil WTI', 'Oil Brent', 'Natural Gas', 'Copper'],
    17→    'US Indices': ['S&P 500', 'NASDAQ', 'Dow Jones', 'Russell 2000'],
    18→    'European Indices': ['DAX', 'FTSE 100', 'CAC 40', 'Euro Stoxx 50'],
    19→    'Crypto': ['BTC/USD', 'ETH/USD', 'BNB/USD', 'SOL/USD'],
    20→    'Tech Stocks': ['AAPL', 'MSFT', 'NVDA', 'GOOGL', 'TSLA', 'META', 'AMZN'],
    21→    'Finance': ['JPM', 'BAC', 'GS', 'MS', 'V', 'MA'],
    22→    'Healthcare': ['JNJ', 'UNH', 'PFE', 'LLY', 'ABBV']
    23→  };
    24→
    25→  // Generate realistic market data
    26→  const generateMarketData = (symbol, category) => {
    27→    const basePrice = category.includes('Crypto') ? 30000 + Math.random() * 40000 :
    28→                     category.includes('Index') ? 15000 + Math.random() * 5000 :
    29→                     category === 'Commodities' ? 1500 + Math.random() * 1500 :
    30→                     category.includes('Stocks') ? 150 + Math.random() * 350 :
    31→                     1.0 + Math.random() * 0.3;
    32→
    33→    const trend = Math.random() > 0.5 ? 'bullish' : 'bearish';
    34→    const trendStrength = 0.5 + Math.random() * 0.5;
    35→    
    36→    return {
    37→      symbol,
    38→      category,
    39→      price: basePrice,
    40→      change24h: (Math.random() - 0.5) * 6,
    41→      volume: Math.random() * 1000000,
    42→      
    43→      // H4 Timeframe
    44→      h4: {
    45→        ema55: basePrice * (trend === 'bullish' ? 0.96 + Math.random() * 0.02 : 1.01 + Math.random() * 0.02),
    46→        ema100: basePrice * (trend === 'bullish' ? 0.94 + Math.random() * 0.02 : 1.03 + Math.random() * 0.02),
    47→        ema200: basePrice * (trend === 'bullish' ? 0.92 + Math.random() * 0.02 : 1.05 + Math.random() * 0.02),
    48→        adx: 18 + Math.random() * 35,
    49→        plusDI: trend === 'bullish' ? 25 + Math.random() * 20 : 15 + Math.random() * 15,
    50→        minusDI: trend === 'bearish' ? 25 + Math.random() * 20 : 15 + Math.random() * 15,
    51→      },
    52→      
    53→      // H1 Timeframe
    54→      h1: {
    55→        ema55: basePrice * (trend === 'bullish' ? 0.98 + Math.random() * 0.015 : 1.01 + Math.random() * 0.015),
    56→        ema26: basePrice * (trend === 'bullish' ? 0.99 + Math.random() * 0.01 : 1.005 + Math.random() * 0.01),
    57→        ema21: basePrice * (trend === 'bullish' ? 0.995 + Math.random() * 0.008 : 1.002 + Math.random() * 0.008),
    58→        
    59→        // Fibonacci levels
    60→        fibLevel: Math.random() < 0.3 ? 'at_618' : Math.random() < 0.5 ? 'at_50' : 'none',
    61→        fibDistance: Math.random() * 0.03,
    62→        candlesSinceSwing: Math.floor(8 + Math.random() * 50),
    63→        
    64→        // Bollinger Bands (1.618 StdDev)
    65→        bollingerUpper: basePrice * 1.025,
    66→        bollingerLower: basePrice * 0.975,
    67→        bollingerMiddle: basePrice,
    68→        bandWidth: 0.02 + Math.random() * 0.03,
    69→        
    70→        // Stochastic (13,5,8)
    71→        stochasticK: Math.random() * 100,
    72→        stochasticD: Math.random() * 100,
    73→        
    74→        // ADX
    75→        adx: 18 + Math.random() * 35,
    76→      },
    77→      
    78→      // Elliott Wave
    79→      elliottWave: {
    80→        wave: Math.random() < 0.3 ? 'wave4_ending' : Math.random() < 0.5 ? 'wave5_starting' : 'unclear',
    81→        impulseClear: Math.random() > 0.5,
    82→      },
    83→      
    84→      trend,
    85→      trendStrength
    86→    };
    87→  };
    88→
    89→  // Calculate comprehensive score (Fibonacci-optimized v2.0)
    90→  const calculateScore = (data) => {
    91→    let score = 0;
    92→    const breakdown = {};
    93→    
    94→    // 1. Fibonacci Levels (34 points) ⭐
    95→    let fibScore = 0;
    96→    if (data.h1.fibLevel === 'at_618' && data.h1.fibDistance < 0.02) {
    97→      fibScore = 34;
    98→      breakdown.fibonacci = { value: 34, max: 34, label: '61.8% Retracement Perfect' };
    99→    } else if (data.h1.fibLevel === 'at_50' && data.h1.fibDistance < 0.02) {
   100→      fibScore = 34;
   101→      breakdown.fibonacci = { value: 34, max: 34, label: '50% Retracement Perfect' };
   102→    } else if (data.h1.fibDistance < 0.03) {
   103→      fibScore = 21;
   104→      breakdown.fibonacci = { value: 21, max: 34, label: 'Near Fibonacci Level' };
   105→    } else {
   106→      breakdown.fibonacci = { value: 0, max: 34, label: 'No Fibonacci Alignment' };
   107→    }
   108→    score += fibScore;
   109→    
   110→    // 2. EMA Structure (21 points)
   111→    let emaScore = 0;
   112→    
   113→    // H4 Trend (10 points)
   114→    if (data.trend === 'bullish') {
   115→      if (data.price > data.h4.ema200) emaScore += 3;
   116→      if (data.price > data.h4.ema100) emaScore += 3;
   117→      if (data.price > data.h4.ema55) emaScore += 4;
   118→    } else {
   119→      if (data.price < data.h4.ema200) emaScore += 3;
   120→      if (data.price < data.h4.ema100) emaScore += 3;
   121→      if (data.price < data.h4.ema55) emaScore += 4;
   122→    }
   123→    
   124→    // H1 Setup (10 points)
   125→    if (data.trend === 'bullish') {
   126→      if (data.price > data.h1.ema55) emaScore += 4;
   127→      if (data.price > data.h1.ema26) emaScore += 3;
   128→      if (data.price > data.h1.ema21) emaScore += 3;
   129→    } else {
   130→      if (data.price < data.h1.ema55) emaScore += 4;
   131→      if (data.price < data.h1.ema26) emaScore += 3;
   132→      if (data.price < data.h1.ema21) emaScore += 3;
   133→    }
   134→    
   135→    // Confluence Bonus (13 points)
   136→    const confluenceElements = [];
   137→    if (Math.abs(data.price - data.h1.ema55) / data.price < 0.005) confluenceElements.push('EMA55');
   138→    if (data.h1.fibDistance < 0.005) confluenceElements.push('Fib');
   139→    if (Math.abs(data.price - data.h1.bollingerLower) / data.price < 0.005) confluenceElements.push('BB');
   140→    
   141→    if (confluenceElements.length >= 3) emaScore += 13;
   142→    else if (confluenceElements.length >= 2) emaScore += 8;
   143→    
   144→    score += emaScore;
   145→    breakdown.ema = { value: emaScore, max: 34, label: `EMA Alignment (Confluence: ${confluenceElements.join(', ')})` };
   146→    
   147→    // 3. Bollinger Bands (13 points) - StdDev 1.618
   148→    let bbScore = 0;
   149→    const distToLower = Math.abs(data.price - data.h1.bollingerLower) / data.price;
   150→    const distToUpper = Math.abs(data.price - data.h1.bollingerUpper) / data.price;
   151→    
   152→    if (data.h1.bandWidth < 0.015) {
   153→      bbScore = 13;
   154→      breakdown.bollinger = { value: 13, max: 13, label: 'Band Squeeze (Breakout Imminent)' };
   155→    } else if (distToLower < 0.005 || distToUpper < 0.005) {
   156→      bbScore = 8;
   157→      breakdown.bollinger = { value: 8, max: 13, label: 'At Band Extreme' };
   158→    } else {
   159→      breakdown.bollinger = { value: 0, max: 13, label: 'Not at Bands' };
   160→    }
   161→    score += bbScore;
   162→    
   163→    // 4. Stochastic (13 points) - 13,5,8
   164→    let stochScore = 0;
   165→    const stochK = data.h1.stochasticK;
   166→    const stochD = data.h1.stochasticD;
   167→    
   168→    if (stochK < 20 && stochK > stochD) {
   169→      stochScore = 10;
   170→      breakdown.stochastic = { value: 10, max: 13, label: 'Oversold Bullish Cross' };
   171→    } else if (stochK > 80 && stochK < stochD) {
   172→      stochScore = 10;
   173→      breakdown.stochastic = { value: 10, max: 13, label: 'Overbought Bearish Cross' };
   174→    } else if (Math.random() < 0.15) { // Simulate divergence
   175→      stochScore = 13;
   176→      breakdown.stochastic = { value: 13, max: 13, label: 'Divergence Detected!' };
   177→    } else {
   178→      breakdown.stochastic = { value: 0, max: 13, label: 'Neutral' };
   179→    }
   180→    score += stochScore;
   181→    
   182→    // 5. ADX (8 points) - Period 13
   183→    let adxScore = 0;
   184→    const adx = data.h1.adx;
   185→    
   186→    if (adx > 40) adxScore = 8;
   187→    else if (adx > 30) adxScore = 6;
   188→    else if (adx > 25) adxScore = 5;
   189→    else if (adx > 20) adxScore = 3;
   190→    
   191→    score += adxScore;
   192→    breakdown.adx = { value: adxScore, max: 8, label: `ADX ${adx.toFixed(1)} (${adx > 25 ? 'Strong' : 'Moderate'})` };
   193→    
   194→    // 6. Elliott Wave (8 points)
   195→    let elliottScore = 0;
   196→    if (data.elliottWave.wave === 'wave4_ending' && data.elliottWave.impulseClear) {
   197→      elliottScore = 8;
   198→      breakdown.elliott = { value: 8, max: 8, label: 'Wave 4 Ending - Wave 5 Setup' };
   199→    } else if (data.elliottWave.wave === 'wave5_starting') {
   200→      elliottScore = 8;
   201→      breakdown.elliott = { value: 8, max: 8, label: 'Wave 5 Starting' };
   202→    } else if (data.elliottWave.impulseClear) {
   203→      elliottScore = 5;
   204→      breakdown.elliott = { value: 5, max: 8, label: 'Clear Impulse Pattern' };
   205→    } else {
   206→      breakdown.elliott = { value: 0, max: 8, label: 'Unclear Structure' };
   207→    }
   208→    score += elliottScore;
   209→    
   210→    // 7. Fibonacci Time Cycles (5 points)
   211→    let fibTimeScore = 0;
   212→    const candles = data.h1.candlesSinceSwing;
   213→    const fibNumbers = [13, 21, 34, 55, 89];
   214→    
   215→    if (fibNumbers.some(fib => Math.abs(candles - fib) <= 2)) {
   216→      fibTimeScore = 5;
   217→      breakdown.fibTime = { value: 5, max: 5, label: `At Fib Time (${candles} candles)` };
   218→    } else {
   219→      breakdown.fibTime = { value: 0, max: 5, label: `Not at Fib Time (${candles} candles)` };
   220→    }
   221→    score += fibTimeScore;
   222→    
   223→    // 8. Volume (3 points)
   224→    const volumeScore = Math.random() > 0.5 ? 3 : 0;
   225→    score += volumeScore;
   226→    breakdown.volume = { value: volumeScore, max: 3, label: volumeScore ? 'High Volume' : 'Low Volume' };
   227→    
   228→    return { score, breakdown };
   229→  };
   230→
   231→  // Calculate trade parameters
   232→  const calculateTradeParams = (data, score) => {
   233→    const entry = data.price;
   234→    const stopLoss = entry * (data.trend === 'bullish' ? 0.9764 : 1.0236); // -2.36% Golden Ratio
   235→    const takeProfit = entry * (data.trend === 'bullish' ? 1.0618 : 0.9382); // +6.18% Golden Ratio
   236→    
   237→    // Leverage based on score (Fibonacci: 8, 5, 3)
   238→    let leverage = 3;
   239→    if (score >= 89) leverage = 8;
   240→    else if (score >= 76) leverage = 5;
   241→    
   242→    const exposure = 10 * (1 + leverage / 100);
   243→    const maxLoss = exposure * 0.0236;
   244→    
   245→    return {
   246→      entry,
   247→      stopLoss,
   248→      takeProfit,
   249→      riskReward: '1:2.618',
   250→      leverage,
   251→      exposure,
   252→      maxLoss
   253→    };
   254→  };
   255→
   256→  // Scan all assets
   257→  const scanMarkets = () => {
   258→    setScanning(true);
   259→    setLastUpdate(new Date());
   260→    
   261→    setTimeout(() => {
   262→      const allSignals = [];
   263→      
   264→      Object.entries(assetCategories).forEach(([category, assets]) => {
   265→        assets.forEach(symbol => {
   266→          const data = generateMarketData(symbol, category);
   267→          const { score, breakdown } = calculateScore(data);
   268→          
   269→          // Only include if score >= threshold
   270→          if (score >= filterScore) {
   271→            const tradeParams = calculateTradeParams(data, score);
   272→            
   273→            allSignals.push({
   274→              symbol,
   275→              category,
   276→              score,
   277→              data,
   278→              breakdown,
   279→              tradeParams,
   280→              direction: data.trend,
   281→              timestamp: new Date()
   282→            });
   283→          }
   284→        });
   285→      });
   286→      
   287→      // Sort by score (highest first)
   288→      allSignals.sort((a, b) => b.score - a.score);
   289→      
   290→      setActiveSignals(allSignals);
   291→      setScanning(false);
   292→    }, 2000);
   293→  };
   294→
   295→  // Auto-refresh every 5 minutes
   296→  useEffect(() => {
   297→    scanMarkets();
   298→    
   299→    if (autoRefresh) {
   300→      const interval = setInterval(() => {
   301→        scanMarkets();
   302→      }, 300000); // 5 minutes
   303→      
   304→      return () => clearInterval(interval);
   305→    }
   306→  }, [autoRefresh, filterScore]);
   307→
   308→  // Filter signals by category
   309→  const filteredSignals = filterCategory === 'all' 
   310→    ? activeSignals 
   311→    : activeSignals.filter(s => s.category === filterCategory);
   312→
   313→  return (
   314→    <div style={{
   315→      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
   316→      background: '#0f172a',
   317→      minHeight: '100vh',
   318→      padding: '20px',
   319→      color: '#e2e8f0'
   320→    }}>
   321→      <div style={{ maxWidth: '1600px', margin: '0 auto' }}>
   322→        {/* Header */}
   323→        <div style={{
   324→          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
   325→          borderRadius: '16px',
   326→          padding: '24px',
   327→          marginBottom: '20px',
   328→          boxShadow: '0 8px 32px rgba(102, 126, 234, 0.3)'
   329→        }}>
   330→          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: '16px' }}>
   331→            <div>
   332→              <h1 style={{ margin: 0, fontSize: '28px', fontWeight: 700, color: 'white' }}>
   333→                🎯 Elliott Wave Live Signals
   334→              </h1>
   335→              <p style={{ margin: '8px 0 0 0', fontSize: '14px', opacity: 0.9 }}>
   336→                Real-time multi-asset screening • Fibonacci-optimized v2.0
   337→              </p>
   338→            </div>
   339→            
   340→            <div style={{ display: 'flex', gap: '12px', alignItems: 'center' }}>
   341→              <div style={{ 
   342→                background: 'rgba(255,255,255,0.2)', 
   343→                padding: '8px 16px', 
   344→                borderRadius: '8px',
   345→                backdropFilter: 'blur(10px)'
   346→              }}>
   347→                <Clock size={16} style={{ verticalAlign: 'middle', marginRight: '6px' }} />
   348→                <span style={{ fontSize: '13px', fontWeight: 600 }}>
   349→                  {lastUpdate.toLocaleTimeString('de-DE')}
   350→                </span>
   351→              </div>
   352→              
   353→              <button
   354→                onClick={scanMarkets}
   355→                disabled={scanning}
   356→                style={{
   357→                  background: scanning ? 'rgba(255,255,255,0.1)' : 'rgba(255,255,255,0.2)',
   358→                  border: 'none',
   359→                  padding: '8px 16px',
   360→                  borderRadius: '8px',
   361→                  color: 'white',
   362→                  fontWeight: 600,
   363→                  cursor: scanning ? 'not-allowed' : 'pointer',
   364→                  backdropFilter: 'blur(10px)',
   365→                  transition: 'all 0.2s',
   366→                  fontSize: '13px'
   367→                }}
   368→              >
   369→                <RefreshCw size={16} style={{ 
   370→                  verticalAlign: 'middle', 
   371→                  marginRight: '6px',
   372→                  animation: scanning ? 'spin 1s linear infinite' : 'none'
   373→                }} />
   374→                {scanning ? 'Scanning...' : 'Refresh'}
   375→              </button>
   376→            </div>
   377→          </div>
   378→        </div>
   379→
   380→        {/* Stats Overview */}
   381→        <div style={{
   382→          display: 'grid',
   383→          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
   384→          gap: '16px',
   385→          marginBottom: '20px'
   386→        }}>
   387→          <StatCard
   388→            icon={<Activity size={20} />}
   389→            label="Active Signals"
   390→            value={filteredSignals.length}
   391→            color="#48bb78"
   392→          />
   393→          <StatCard
   394→            icon={<Target size={20} />}
   395→            label="Top Score"
   396→            value={filteredSignals.length > 0 ? `${filteredSignals[0].score}/100` : '--'}
   397→            color="#667eea"
   398→          />
   399→          <StatCard
   400→            icon={<TrendingUp size={20} />}
   401→            label="Long Setups"
   402→            value={filteredSignals.filter(s => s.direction === 'bullish').length}
   403→            color="#4ade80"
   404→          />
   405→          <StatCard
   406→            icon={<TrendingDown size={20} />}
   407→            label="Short Setups"
   408→            value={filteredSignals.filter(s => s.direction === 'bearish').length}
   409→            color="#f87171"
   410→          />
   411→        </div>
   412→
   413→        {/* Filters */}
   414→        <div style={{
   415→          background: '#1e293b',
   416→          borderRadius: '12px',
   417→          padding: '16px',
   418→          marginBottom: '20px',
   419→          display: 'flex',
   420→          gap: '16px',
   421→          alignItems: 'center',
   422→          flexWrap: 'wrap'
   423→        }}>
   424→          <Filter size={18} style={{ color: '#94a3b8' }} />
   425→          
   426→          <select
   427→            value={filterCategory}
   428→            onChange={(e) => setFilterCategory(e.target.value)}
   429→            style={{
   430→              background: '#0f172a',
   431→              border: '1px solid #334155',
   432→              borderRadius: '8px',
   433→              padding: '8px 12px',
   434→              color: '#e2e8f0',
   435→              fontSize: '13px',
   436→              cursor: 'pointer'
   437→            }}
   438→          >
   439→            <option value="all">All Categories</option>
   440→            {Object.keys(assetCategories).map(cat => (
   441→              <option key={cat} value={cat}>{cat}</option>
   442→            ))}
   443→          </select>
   444→
   445→          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
   446→            <label style={{ fontSize: '13px', color: '#94a3b8' }}>Min Score:</label>
   447→            <input
   448→              type="number"
   449→              min="70"
   450→              max="95"
   451→              step="5"
   452→              value={filterScore}
   453→              onChange={(e) => setFilterScore(parseInt(e.target.value))}
   454→              style={{
   455→                background: '#0f172a',
   456→                border: '1px solid #334155',
   457→                borderRadius: '8px',
   458→                padding: '8px 12px',
   459→                color: '#e2e8f0',
   460→                fontSize: '13px',
   461→                width: '70px'
   462→              }}
   463→            />
   464→          </div>
   465→
   466→          <label style={{ 
   467→            marginLeft: 'auto',
   468→            display: 'flex',
   469→            alignItems: 'center',
   470→            gap: '8px',
   471→            cursor: 'pointer',
   472→            fontSize: '13px'
   473→          }}>
   474→            <input
   475→              type="checkbox"
   476→              checked={autoRefresh}
   477→              onChange={(e) => setAutoRefresh(e.target.checked)}
   478→              style={{ cursor: 'pointer' }}
   479→            />
   480→            Auto-refresh (5min)
   481→          </label>
   482→        </div>
   483→
   484→        {/* Signals Grid */}
   485→        {scanning ? (
   486→          <div style={{
   487→            background: '#1e293b',
   488→            borderRadius: '12px',
   489→            padding: '60px',
   490→            textAlign: 'center'
   491→          }}>
   492→            <Activity size={48} style={{ color: '#667eea', marginBottom: '16px' }} />
   493→            <h3 style={{ margin: 0, color: '#e2e8f0' }}>Scanning Markets...</h3>
   494→            <p style={{ margin: '8px 0 0 0', color: '#94a3b8', fontSize: '14px' }}>
   495→              Analyzing {Object.values(assetCategories).flat().length} assets
   496→            </p>
   497→          </div>
   498→        ) : filteredSignals.length === 0 ? (
   499→          <div style={{
   500→            background: '#1e293b',
   501→            borderRadius: '12px',
   502→            padding: '60px',
   503→            textAlign: 'center'
   504→          }}>
   505→            <AlertCircle size={48} style={{ color: '#94a3b8', marginBottom: '16px' }} />
   506→            <h3 style={{ margin: 0, color: '#e2e8f0' }}>No Qualified Signals</h3>
   507→            <p style={{ margin: '8px 0 0 0', color: '#94a3b8', fontSize: '14px' }}>
   508→              No setups found with score ≥ {filterScore}
   509→            </p>
   510→          </div>
   511→        ) : (
   512→          <div style={{ display: 'grid', gap: '16px' }}>
   513→            {filteredSignals.map((signal, index) => (
   514→              <SignalCard
   515→                key={`${signal.symbol}-${index}`}
