import React, { useState, useEffect } from 'react';
import { TrendingUp, TrendingDown, Activity, AlertCircle, CheckCircle, Clock, Target, DollarSign, BarChart3, Zap, Filter, RefreshCw } from 'lucide-react';

const ElliottWaveLiveSignals = () => {
  const [activeSignals, setActiveSignals] = useState([]);
  const [scanning, setScanning] = useState(false);
  const [selectedSignal, setSelectedSignal] = useState(null);
  const [autoRefresh, setAutoRefresh] = useState(true);
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [filterCategory, setFilterCategory] = useState('all');
  const [filterScore, setFilterScore] = useState(75);

  // Expanded Asset Universe
  const assetCategories = {
    'Forex Majors': ['EUR/USD', 'GBP/USD', 'USD/JPY', 'AUD/USD', 'USD/CHF', 'USD/CAD', 'NZD/USD'],
    'Commodities': ['Gold', 'Silver', 'Oil WTI', 'Oil Brent', 'Natural Gas', 'Copper'],
    'US Indices': ['S&P 500', 'NASDAQ', 'Dow Jones', 'Russell 2000'],
    'European Indices': ['DAX', 'FTSE 100', 'CAC 40', 'Euro Stoxx 50'],
    'Crypto': ['BTC/USD', 'ETH/USD', 'BNB/USD', 'SOL/USD'],
    'Tech Stocks': ['AAPL', 'MSFT', 'NVDA', 'GOOGL', 'TSLA', 'META', 'AMZN'],
    'Finance': ['JPM', 'BAC', 'GS', 'MS', 'V', 'MA'],
    'Healthcare': ['JNJ', 'UNH', 'PFE', 'LLY', 'ABBV']
  };

  // Generate realistic market data
  const generateMarketData = (symbol, category) => {
    const basePrice = category.includes('Crypto') ? 30000 + Math.random() * 40000 :
                     category.includes('Index') ? 15000 + Math.random() * 5000 :
                     category === 'Commodities' ? 1500 + Math.random() * 1500 :
                     category.includes('Stocks') ? 150 + Math.random() * 350 :
                     1.0 + Math.random() * 0.3;

    const trend = Math.random() > 0.5 ? 'bullish' : 'bearish';
    const trendStrength = 0.5 + Math.random() * 0.5;

    return {
      symbol,
      category,
      price: basePrice,
      change24h: (Math.random() - 0.5) * 6,
      volume: Math.random() * 1000000,

      // H4 Timeframe
      h4: {
        ema55: basePrice * (trend === 'bullish' ? 0.96 + Math.random() * 0.02 : 1.01 + Math.random() * 0.02),
        ema100: basePrice * (trend === 'bullish' ? 0.94 + Math.random() * 0.02 : 1.03 + Math.random() * 0.02),
        ema200: basePrice * (trend === 'bullish' ? 0.92 + Math.random() * 0.02 : 1.05 + Math.random() * 0.02),
        adx: 18 + Math.random() * 35,
        plusDI: trend === 'bullish' ? 25 + Math.random() * 20 : 15 + Math.random() * 15,
        minusDI: trend === 'bearish' ? 25 + Math.random() * 20 : 15 + Math.random() * 15,
      },

      // H1 Timeframe
      h1: {
        ema55: basePrice * (trend === 'bullish' ? 0.98 + Math.random() * 0.015 : 1.01 + Math.random() * 0.015),
        ema26: basePrice * (trend === 'bullish' ? 0.99 + Math.random() * 0.01 : 1.005 + Math.random() * 0.01),
        ema21: basePrice * (trend === 'bullish' ? 0.995 + Math.random() * 0.008 : 1.002 + Math.random() * 0.008),

        // Fibonacci levels
        fibLevel: Math.random() < 0.3 ? 'at_618' : Math.random() < 0.5 ? 'at_50' : 'none',
        fibDistance: Math.random() * 0.03,
        candlesSinceSwing: Math.floor(8 + Math.random() * 50),

        // Bollinger Bands (1.618 StdDev)
        bollingerUpper: basePrice * 1.025,
        bollingerLower: basePrice * 0.975,
        bollingerMiddle: basePrice,
        bandWidth: 0.02 + Math.random() * 0.03,

        // Stochastic (13,5,8)
        stochasticK: Math.random() * 100,
        stochasticD: Math.random() * 100,

        // ADX
        adx: 18 + Math.random() * 35,
      },

      // Elliott Wave
      elliottWave: {
        wave: Math.random() < 0.3 ? 'wave4_ending' : Math.random() < 0.5 ? 'wave5_starting' : 'unclear',
        impulseClear: Math.random() > 0.5,
      },

      trend,
      trendStrength
    };
  };

  // Calculate comprehensive score (Fibonacci-optimized v2.0)
  const calculateScore = (data) => {
    let score = 0;
    const breakdown = {};

    // 1. Fibonacci Levels (34 points) ⭐
    let fibScore = 0;
    if (data.h1.fibLevel === 'at_618' && data.h1.fibDistance < 0.02) {
      fibScore = 34;
      breakdown.fibonacci = { value: 34, max: 34, label: '61.8% Retracement Perfect' };
    } else if (data.h1.fibLevel === 'at_50' && data.h1.fibDistance < 0.02) {
      fibScore = 34;
      breakdown.fibonacci = { value: 34, max: 34, label: '50% Retracement Perfect' };
    } else if (data.h1.fibDistance < 0.03) {
      fibScore = 21;
      breakdown.fibonacci = { value: 21, max: 34, label: 'Near Fibonacci Level' };
    } else {
      breakdown.fibonacci = { value: 0, max: 34, label: 'No Fibonacci Alignment' };
    }
    score += fibScore;

    // 2. EMA Structure (21 points)
    let emaScore = 0;

    // H4 Trend (10 points)
    if (data.trend === 'bullish') {
      if (data.price > data.h4.ema200) emaScore += 3;
      if (data.price > data.h4.ema100) emaScore += 3;
      if (data.price > data.h4.ema55) emaScore += 4;
    } else {
      if (data.price < data.h4.ema200) emaScore += 3;
      if (data.price < data.h4.ema100) emaScore += 3;
      if (data.price < data.h4.ema55) emaScore += 4;
    }

    // H1 Setup (10 points)
    if (data.trend === 'bullish') {
      if (data.price > data.h1.ema55) emaScore += 4;
      if (data.price > data.h1.ema26) emaScore += 3;
      if (data.price > data.h1.ema21) emaScore += 3;
    } else {
      if (data.price < data.h1.ema55) emaScore += 4;
      if (data.price < data.h1.ema26) emaScore += 3;
      if (data.price < data.h1.ema21) emaScore += 3;
    }

    // Confluence Bonus (13 points)
    const confluenceElements = [];
    if (Math.abs(data.price - data.h1.ema55) / data.price < 0.005) confluenceElements.push('EMA55');
    if (data.h1.fibDistance < 0.005) confluenceElements.push('Fib');
    if (Math.abs(data.price - data.h1.bollingerLower) / data.price < 0.005) confluenceElements.push('BB');

    if (confluenceElements.length >= 3) emaScore += 13;
    else if (confluenceElements.length >= 2) emaScore += 8;

    score += emaScore;
    breakdown.ema = { value: emaScore, max: 34, label: `EMA Alignment (Confluence: ${confluenceElements.join(', ')})` };

    // 3. Bollinger Bands (13 points) - StdDev 1.618
    let bbScore = 0;
    const distToLower = Math.abs(data.price - data.h1.bollingerLower) / data.price;
    const distToUpper = Math.abs(data.price - data.h1.bollingerUpper) / data.price;

    if (data.h1.bandWidth < 0.015) {
      bbScore = 13;
      breakdown.bollinger = { value: 13, max: 13, label: 'Band Squeeze (Breakout Imminent)' };
    } else if (distToLower < 0.005 || distToUpper < 0.005) {
      bbScore = 8;
      breakdown.bollinger = { value: 8, max: 13, label: 'At Band Extreme' };
    } else {
      breakdown.bollinger = { value: 0, max: 13, label: 'Not at Bands' };
    }
    score += bbScore;

    // 4. Stochastic (13 points) - 13,5,8
    let stochScore = 0;
    const stochK = data.h1.stochasticK;
    const stochD = data.h1.stochasticD;

    if (stochK < 20 && stochK > stochD) {
      stochScore = 10;
      breakdown.stochastic = { value: 10, max: 13, label: 'Oversold Bullish Cross' };
    } else if (stochK > 80 && stochK < stochD) {
      stochScore = 10;
      breakdown.stochastic = { value: 10, max: 13, label: 'Overbought Bearish Cross' };
    } else if (Math.random() < 0.15) { // Simulate divergence
      stochScore = 13;
      breakdown.stochastic = { value: 13, max: 13, label: 'Divergence Detected!' };
    } else {
      breakdown.stochastic = { value: 0, max: 13, label: 'Neutral' };
    }
    score += stochScore;

    // 5. ADX (8 points) - Period 13
    let adxScore = 0;
    const adx = data.h1.adx;

    if (adx > 40) adxScore = 8;
    else if (adx > 30) adxScore = 6;
    else if (adx > 25) adxScore = 5;
    else if (adx > 20) adxScore = 3;

    score += adxScore;
    breakdown.adx = { value: adxScore, max: 8, label: `ADX ${adx.toFixed(1)} (${adx > 25 ? 'Strong' : 'Moderate'})` };

    // 6. Elliott Wave (8 points)
    let elliottScore = 0;
    if (data.elliottWave.wave === 'wave4_ending' && data.elliottWave.impulseClear) {
      elliottScore = 8;
      breakdown.elliott = { value: 8, max: 8, label: 'Wave 4 Ending - Wave 5 Setup' };
    } else if (data.elliottWave.wave === 'wave5_starting') {
      elliottScore = 8;
      breakdown.elliott = { value: 8, max: 8, label: 'Wave 5 Starting' };
    } else if (data.elliottWave.impulseClear) {
      elliottScore = 5;
      breakdown.elliott = { value: 5, max: 8, label: 'Clear Impulse Pattern' };
    } else {
      breakdown.elliott = { value: 0, max: 8, label: 'Unclear Structure' };
    }
    score += elliottScore;

    // 7. Fibonacci Time Cycles (5 points)
    let fibTimeScore = 0;
    const candles = data.h1.candlesSinceSwing;
    const fibNumbers = [13, 21, 34, 55, 89];

    if (fibNumbers.some(fib => Math.abs(candles - fib) <= 2)) {
      fibTimeScore = 5;
      breakdown.fibTime = { value: 5, max: 5, label: `At Fib Time (${candles} candles)` };
    } else {
      breakdown.fibTime = { value: 0, max: 5, label: `Not at Fib Time (${candles} candles)` };
    }
    score += fibTimeScore;

    // 8. Volume (3 points)
    const volumeScore = Math.random() > 0.5 ? 3 : 0;
    score += volumeScore;
    breakdown.volume = { value: volumeScore, max: 3, label: volumeScore ? 'High Volume' : 'Low Volume' };

    return { score, breakdown };
  };

  // Calculate trade parameters
  const calculateTradeParams = (data, score) => {
    const entry = data.price;
    const stopLoss = entry * (data.trend === 'bullish' ? 0.9764 : 1.0236); // -2.36% Golden Ratio
    const takeProfit = entry * (data.trend === 'bullish' ? 1.0618 : 0.9382); // +6.18% Golden Ratio

    // Leverage based on score (Fibonacci: 8, 5, 3)
    let leverage = 3;
    if (score >= 89) leverage = 8;
    else if (score >= 76) leverage = 5;

    const exposure = 10 * (1 + leverage / 100);
    const maxLoss = exposure * 0.0236;

    return {
      entry,
      stopLoss,
      takeProfit,
      riskReward: '1:2.618',
      leverage,
      exposure,
      maxLoss
    };
  };

  // Scan all assets
  const scanMarkets = () => {
    setScanning(true);
    setLastUpdate(new Date());

    setTimeout(() => {
      const allSignals = [];

      Object.entries(assetCategories).forEach(([category, assets]) => {
        assets.forEach(symbol => {
          const data = generateMarketData(symbol, category);
          const { score, breakdown } = calculateScore(data);

          // Only include if score >= threshold
          if (score >= filterScore) {
            const tradeParams = calculateTradeParams(data, score);

            allSignals.push({
              symbol,
              category,
              score,
              data,
              breakdown,
              tradeParams,
              direction: data.trend,
              timestamp: new Date()
            });
          }
        });
      });

      // Sort by score (highest first)
      allSignals.sort((a, b) => b.score - a.score);

      setActiveSignals(allSignals);
      setScanning(false);
    }, 2000);
  };

  // Auto-refresh every 5 minutes
  useEffect(() => {
    scanMarkets();

    if (autoRefresh) {
      const interval = setInterval(() => {
        scanMarkets();
      }, 300000); // 5 minutes

      return () => clearInterval(interval);
    }
  }, [autoRefresh, filterScore]);

  // Filter signals by category
  const filteredSignals = filterCategory === 'all' 
    ? activeSignals 
    : activeSignals.filter(s => s.category === filterCategory);

  return (
    <div style={{
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      background: '#0f172a',
      minHeight: '100vh',
      padding: '20px',
      color: '#e2e8f0'
    }}>
      <div style={{ maxWidth: '1600px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          borderRadius: '16px',
          padding: '24px',
          marginBottom: '20px',
          boxShadow: '0 8px 32px rgba(102, 126, 234, 0.3)'
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: '16px' }}>
            <div>
              <h1 style={{ margin: 0, fontSize: '28px', fontWeight: 700, color: 'white' }}>
                🎯 Elliott Wave Live Signals
              </h1>
              <p style={{ margin: '8px 0 0 0', fontSize: '14px', opacity: 0.9 }}>
                Real-time multi-asset screening • Fibonacci-optimized v2.0
              </p>
            </div>

            <div style={{ display: 'flex', gap: '12px', alignItems: 'center' }}>
              <div style={{ 
                background: 'rgba(255,255,255,0.2)', 
                padding: '8px 16px', 
                borderRadius: '8px',
                backdropFilter: 'blur(10px)'
              }}>
                <Clock size={16} style={{ verticalAlign: 'middle', marginRight: '6px' }} />
                <span style={{ fontSize: '13px', fontWeight: 600 }}>
                  {lastUpdate.toLocaleTimeString('de-DE')}
                </span>
              </div>

              <button
                onClick={scanMarkets}
                disabled={scanning}
                style={{
                  background: scanning ? 'rgba(255,255,255,0.1)' : 'rgba(255,255,255,0.2)',
                  border: 'none',
                  padding: '8px 16px',
                  borderRadius: '8px',
                  color: 'white',
                  fontWeight: 600,
                  cursor: scanning ? 'not-allowed' : 'pointer',
                  backdropFilter: 'blur(10px)',
                  transition: 'all 0.2s',
                  fontSize: '13px'
                }}
              >
                <RefreshCw size={16} style={{ 
                  verticalAlign: 'middle', 
                  marginRight: '6px',
                  animation: scanning ? 'spin 1s linear infinite' : 'none'
                }} />
                {scanning ? 'Scanning...' : 'Refresh'}
              </button>
            </div>
          </div>
        </div>

        {/* Stats Overview */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: '16px',
          marginBottom: '20px'
        }}>
          <StatCard
            icon={<Activity size={20} />}
            label="Active Signals"
            value={filteredSignals.length}
            color="#48bb78"
          />
          <StatCard
            icon={<Target size={20} />}
            label="Top Score"
            value={filteredSignals.length > 0 ? `${filteredSignals[0].score}/100` : '--'}
            color="#667eea"
          />
          <StatCard
            icon={<TrendingUp size={20} />}
            label="Long Setups"
            value={filteredSignals.filter(s => s.direction === 'bullish').length}
            color="#4ade80"
          />
          <StatCard
            icon={<TrendingDown size={20} />}
            label="Short Setups"
            value={filteredSignals.filter(s => s.direction === 'bearish').length}
            color="#f87171"
          />
        </div>

        {/* Filters */}
        <div style={{
          background: '#1e293b',
          borderRadius: '12px',
          padding: '16px',
          marginBottom: '20px',
          display: 'flex',
          gap: '16px',
          alignItems: 'center',
          flexWrap: 'wrap'
        }}>
          <Filter size={18} style={{ color: '#94a3b8' }} />

          <select
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
            style={{
              background: '#0f172a',
              border: '1px solid #334155',
              borderRadius: '8px',
              padding: '8px 12px',
              color: '#e2e8f0',
              fontSize: '13px',
              cursor: 'pointer'
            }}
          >
            <option value="all">All Categories</option>
            {Object.keys(assetCategories).map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>

          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <label style={{ fontSize: '13px', color: '#94a3b8' }}>Min Score:</label>
            <input
              type="number"
              min="70"
              max="95"
              step="5"
              value={filterScore}
              onChange={(e) => setFilterScore(parseInt(e.target.value))}
              style={{
                background: '#0f172a',
                border: '1px solid #334155',
                borderRadius: '8px',
                padding: '8px 12px',
                color: '#e2e8f0',
                fontSize: '13px',
                width: '70px'
              }}
            />
          </div>

          <label style={{ 
            marginLeft: 'auto',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            cursor: 'pointer',
            fontSize: '13px'
          }}>
            <input
              type="checkbox"
              checked={autoRefresh}
              onChange={(e) => setAutoRefresh(e.target.checked)}
              style={{ cursor: 'pointer' }}
            />
            Auto-refresh (5min)
          </label>
        </div>

        {/* Signals Grid */}
        {scanning ? (
          <div style={{
            background: '#1e293b',
            borderRadius: '12px',
            padding: '60px',
            textAlign: 'center'
          }}>
            <Activity size={48} style={{ color: '#667eea', marginBottom: '16px' }} />
            <h3 style={{ margin: 0, color: '#e2e8f0' }}>Scanning Markets...</h3>
            <p style={{ margin: '8px 0 0 0', color: '#94a3b8', fontSize: '14px' }}>
              Analyzing {Object.values(assetCategories).flat().length} assets
            </p>
          </div>
        ) : filteredSignals.length === 0 ? (
          <div style={{
            background: '#1e293b',
            borderRadius: '12px',
            padding: '60px',
            textAlign: 'center'
          }}>
            <AlertCircle size={48} style={{ color: '#94a3b8', marginBottom: '16px' }} />
            <h3 style={{ margin: 0, color: '#e2e8f0' }}>No Qualified Signals</h3>
            <p style={{ margin: '8px 0 0 0', color: '#94a3b8', fontSize: '14px' }}>
              No setups found with score ≥ {filterScore}
            </p>
          </div>
        ) : (
          <div style={{ display: 'grid', gap: '16px' }}>
            {filteredSignals.map((signal, index) => (
              <SignalCard
                key={`${signal.symbol}-${index}`}
                signal={signal}
                rank={index + 1}
                onClick={() => setSelectedSignal(signal)}
                isSelected={selectedSignal?.symbol === signal.symbol}
              />
            ))}
          </div>
        )}

        {/* Detailed Analysis Modal */}
        {selectedSignal && (
          <SignalDetailModal
            signal={selectedSignal}
            onClose={() => setSelectedSignal(null)}
          />
        )}
      </div>

      <style>{`
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

// Stat Card Component
const StatCard = ({ icon, label, value, color }) => (
  <div style={{
    background: '#1e293b',
    borderRadius: '12px',
    padding: '20px',
    borderLeft: `4px solid ${color}`
  }}>
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
      <div>
        <div style={{ fontSize: '12px', color: '#94a3b8', marginBottom: '4px', textTransform: 'uppercase', letterSpacing: '0.5px' }}>
          {label}
        </div>
        <div style={{ fontSize: '28px', fontWeight: 700, color: '#e2e8f0' }}>
          {value}
        </div>
      </div>
      <div style={{ color, opacity: 0.6 }}>
        {icon}
      </div>
    </div>
  </div>
);

// Signal Card Component
const SignalCard = ({ signal, rank, onClick, isSelected }) => (
  <div
    onClick={onClick}
    style={{
      background: isSelected ? '#1e293b' : '#1e293b',
      border: isSelected ? '2px solid #667eea' : '1px solid #334155',
      borderRadius: '12px',
      padding: '20px',
      cursor: 'pointer',
      transition: 'all 0.2s',
      position: 'relative',
      borderLeft: `4px solid ${signal.direction === 'bullish' ? '#4ade80' : '#f87171'}`
    }}
    onMouseEnter={(e) => {
      if (!isSelected) e.currentTarget.style.borderColor = '#475569';
    }}
    onMouseLeave={(e) => {
      if (!isSelected) e.currentTarget.style.borderColor = '#334155';
    }}
  >
    {/* Rank Badge */}
    {rank <= 3 && (
      <div style={{
        position: 'absolute',
        top: '-10px',
        right: '16px',
        background: rank === 1 ? '#fbbf24' : rank === 2 ? '#94a3b8' : '#cd7f32',
        color: '#0f172a',
        padding: '4px 12px',
        borderRadius: '12px',
        fontSize: '11px',
        fontWeight: 700,
        boxShadow: '0 4px 12px rgba(0,0,0,0.3)'
      }}>
        {rank === 1 ? '🥇' : rank === 2 ? '🥈' : '🥉'} #{rank}
      </div>
    )}

    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '16px' }}>
      <div style={{ flex: 1 }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '8px' }}>
          <h3 style={{ margin: 0, fontSize: '20px', fontWeight: 700, color: '#e2e8f0' }}>
            {signal.symbol}
          </h3>

          <span style={{
            padding: '4px 10px',
            borderRadius: '6px',
            fontSize: '11px',
            fontWeight: 600,
            textTransform: 'uppercase',
            background: signal.direction === 'bullish' ? 'rgba(74, 222, 128, 0.2)' : 'rgba(248, 113, 113, 0.2)',
            color: signal.direction === 'bullish' ? '#4ade80' : '#f87171'
          }}>
            {signal.direction === 'bullish' ? '🚀 LONG' : '📉 SHORT'}
          </span>

          <span style={{
            padding: '4px 8px',
            borderRadius: '6px',
            fontSize: '10px',
            fontWeight: 600,
            background: '#334155',
            color: '#94a3b8'
          }}>
            {signal.category}
          </span>
        </div>

        <div style={{ fontSize: '13px', color: '#94a3b8' }}>
          ${signal.data.price.toFixed(signal.category.includes('Forex') ? 4 : 2)} •{' '}
          <span style={{ color: signal.data.change24h > 0 ? '#4ade80' : '#f87171' }}>
            {signal.data.change24h > 0 ? '+' : ''}{signal.data.change24h.toFixed(2)}%
          </span>
          {' '}• ADX {signal.data.h1.adx.toFixed(1)}
        </div>
      </div>

      <div style={{
        fontSize: '32px',
        fontWeight: 800,
        color: signal.score >= 85 ? '#4ade80' : '#667eea',
        padding: '8px 16px',
        background: signal.score >= 85 ? 'rgba(74, 222, 128, 0.1)' : 'rgba(102, 126, 234, 0.1)',
        borderRadius: '10px'
      }}>
        {signal.score}
      </div>
    </div>

    {/* Key Signals */}
    <div style={{
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
      gap: '8px',
      fontSize: '11px'
    }}>
      <SignalBadge
        icon={<Target size={12} />}
        label="Fib Level"
        value={signal.breakdown.fibonacci.label.includes('Perfect') ? '✓ Perfect' : signal.breakdown.fibonacci.label}
        positive={signal.breakdown.fibonacci.value > 20}
      />
      <SignalBadge
        icon={<Activity size={12} />}
        label="EMA"
        value={`${signal.breakdown.ema.value}/${signal.breakdown.ema.max}`}
        positive={signal.breakdown.ema.value > 20}
      />
      <SignalBadge
        icon={<BarChart3 size={12} />}
        label="Elliott"
        value={signal.breakdown.elliott.label.includes('Wave') ? '✓ Wave Pattern' : 'Clear'}
        positive={signal.breakdown.elliott.value >= 5}
      />
      <SignalBadge
        icon={<Zap size={12} />}
        label="Entry"
        value={`$${signal.tradeParams.entry.toFixed(2)}`}
        positive={true}
      />
    </div>
  </div>
);

// Signal Badge Component
const SignalBadge = ({ icon, label, value, positive }) => (
  <div style={{
    background: '#0f172a',
    padding: '8px 10px',
    borderRadius: '6px',
    display: 'flex',
    alignItems: 'center',
    gap: '6px'
  }}>
    <div style={{ color: positive ? '#4ade80' : '#94a3b8' }}>
      {icon}
    </div>
    <div style={{ flex: 1 }}>
      <div style={{ color: '#94a3b8', fontSize: '9px', marginBottom: '2px' }}>{label}</div>
      <div style={{ color: '#e2e8f0', fontWeight: 600, fontSize: '11px' }}>{value}</div>
    </div>
  </div>
);

// Detailed Signal Modal
const SignalDetailModal = ({ signal, onClose }) => (
  <div
    style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0,0,0,0.8)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      zIndex: 1000
    }}
    onClick={onClose}
  >
    <div
      style={{
        background: '#1e293b',
        borderRadius: '16px',
        padding: '32px',
        maxWidth: '900px',
        width: '100%',
        maxHeight: '90vh',
        overflowY: 'auto',
        position: 'relative'
      }}
      onClick={(e) => e.stopPropagation()}
    >
      <button
        onClick={onClose}
        style={{
          position: 'absolute',
          top: '16px',
          right: '16px',
          background: '#334155',
          border: 'none',
          borderRadius: '8px',
          padding: '8px 12px',
          color: '#e2e8f0',
          cursor: 'pointer',
          fontWeight: 600
        }}
      >
        ✕ Close
      </button>

      <h2 style={{ margin: '0 0 24px 0', fontSize: '24px', color: '#e2e8f0' }}>
        {signal.symbol} - Deep Analysis
      </h2>

      {/* Score Breakdown */}
      <div style={{
        background: '#0f172a',
        borderRadius: '12px',
        padding: '20px',
        marginBottom: '20px'
      }}>
        <h3 style={{ margin: '0 0 16px 0', fontSize: '16px', color: '#e2e8f0' }}>
          📊 Score Breakdown (v2.0 Fibonacci-optimized)
        </h3>
        {Object.entries(signal.breakdown).map(([key, data]) => {
          const percentage = (data.value / data.max) * 100;
          return (
            <div key={key} style={{ marginBottom: '12px' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '12px', marginBottom: '6px' }}>
                <span style={{ color: '#94a3b8', textTransform: 'capitalize' }}>{key}</span>
                <span style={{ fontWeight: 700, color: '#e2e8f0' }}>{data.value}/{data.max}</span>
              </div>
              <div style={{ 
                height: '8px', 
                background: '#334155', 
                borderRadius: '4px',
                overflow: 'hidden'
              }}>
                <div style={{ 
                  width: `${percentage}%`, 
                  height: '100%', 
                  background: percentage > 70 ? '#4ade80' : percentage > 40 ? '#fbbf24' : '#f87171',
                  transition: 'width 0.5s ease'
                }} />
              </div>
              <div style={{ fontSize: '11px', color: '#94a3b8', marginTop: '4px' }}>
                {data.label}
              </div>
            </div>
          );
        })}
      </div>

      {/* Trade Parameters */}
      <div style={{
        background: '#0f172a',
        borderRadius: '12px',
        padding: '20px'
      }}>
        <h3 style={{ margin: '0 0 16px 0', fontSize: '16px', color: '#e2e8f0' }}>
          💰 Trade Parameters (Golden Ratio)
        </h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '16px' }}>
          <ParamItem label="Entry Price" value={`$${signal.tradeParams.entry.toFixed(4)}`} />
          <ParamItem label="Stop Loss" value={`$${signal.tradeParams.stopLoss.toFixed(4)}`} color="#f87171" />
          <ParamItem label="Take Profit" value={`$${signal.tradeParams.takeProfit.toFixed(4)}`} color="#4ade80" />
          <ParamItem label="R/R Ratio" value={signal.tradeParams.riskReward} />
          <ParamItem label="Leverage" value={`${signal.tradeParams.leverage}%`} />
          <ParamItem label="Exposure" value={`${signal.tradeParams.exposure.toFixed(2)}€`} />
          <ParamItem label="Max Loss" value={`${signal.tradeParams.maxLoss.toFixed(2)}€`} color="#f87171" />
          <ParamItem label="Potential Gain" value={`${(signal.tradeParams.exposure * 0.0618).toFixed(2)}€`} color="#4ade80" />
        </div>
      </div>
    </div>
  </div>
);

// Parameter Item Component
const ParamItem = ({ label, value, color = '#e2e8f0' }) => (
  <div style={{
    background: '#1e293b',
    padding: '12px',
    borderRadius: '8px'
  }}>
    <div style={{ fontSize: '11px', color: '#94a3b8', marginBottom: '4px' }}>
      {label}
    </div>
    <div style={{ fontSize: '16px', fontWeight: 700, color }}>
      {value}
    </div>
  </div>
);

export default ElliottWaveLiveSignals;

