   201â†’<body>
   202â†’    <div class="container">
   203â†’        <h1>ðŸ“Š Elliott Wave Trading Signals</h1>
   204â†’        <div class="subtitle" id="lastUpdate">Loading...</div>
   205â†’
   206â†’        <div class="stats-bar">
   207â†’            <div class="stat-item">
   208â†’                <div class="stat-label">Total Signals</div>
   209â†’                <div class="stat-value" id="totalSignals">-</div>
   210â†’            </div>
   211â†’            <div class="stat-item">
   212â†’                <div class="stat-label">Long Signals</div>
   213â†’                <div class="stat-value" id="longSignals">-</div>
   214â†’            </div>
   215â†’            <div class="stat-item">
   216â†’                <div class="stat-label">Short Signals</div>
   217â†’                <div class="stat-value" id="shortSignals">-</div>
   218â†’            </div>
   219â†’            <div class="stat-item">
   220â†’                <div class="stat-label">Avg Score</div>
   221â†’                <div class="stat-value" id="avgScore">-</div>
   222â†’            </div>
   223â†’            <div class="stat-item">
   224â†’                <div class="stat-label">Avg R:R</div>
   225â†’                <div class="stat-value" id="avgRR">-</div>
   226â†’            </div>
   227â†’        </div>
   228â†’
   229â†’        <div class="signals-table-container" id="signalsContainer">
   230â†’            <div class="loading">Loading signals...</div>
   231â†’        </div>
   232â†’
   233â†’        <div class="footer">
   234â†’            <p>ðŸ¤– Elliott Wave Trading Bot - Signal Dashboard</p>
   235â†’            <p>Data from Capital.com API (Demo Mode)</p>
   236â†’        </div>
   237â†’    </div>
   238â†’
   239â†’    <script>
   240â†’        // Calculate Risk:Reward ratio
   241â†’        function calculateRR(entry, sl, tp, direction) {
   242â†’            const risk = Math.abs(entry - sl);
   243â†’            const reward = Math.abs(tp - entry);
   244â†’            if (risk === 0) return 0;
   245â†’            return reward / risk;
   246â†’        }
   247â†’
   248â†’        // Format price with appropriate decimals
   249â†’        function formatPrice(price, symbol) {
   250â†’            const decimals = symbol.includes('BTC') || symbol.includes('ETH') ? 2 : 5;
   251â†’            return parseFloat(price).toFixed(decimals);
   252â†’        }
   253â†’
   254â†’        // Format timestamp
   255â†’        function formatTimestamp(timestamp) {
   256â†’            const date = new Date(timestamp);
   257â†’            return date.toLocaleString('de-DE', {
   258â†’                day: '2-digit',
   259â†’                month: '2-digit',
   260â†’                year: 'numeric',
   261â†’                hour: '2-digit',
   262â†’                minute: '2-digit'
   263â†’            });
   264â†’        }
   265â†’
   266â†’        // Get score class based on value
   267â†’        function getScoreClass(score) {
   268â†’            if (score >= 100) return 'score-high';
   269â†’            if (score >= 80) return 'score-medium';
   270â†’            return 'score-value';
   271â†’        }
   272â†’
   273â†’        // Toggle chart visibility
   274â†’        function toggleChart(signalId) {
   275â†’            const chartContainer = document.getElementById(`chart-${signalId}`);
   276â†’            const expandIcon = document.getElementById(`icon-${signalId}`);
   277â†’            const row = document.getElementById(`row-${signalId}`);
   278â†’
   279â†’            if (chartContainer.classList.contains('visible')) {
   280â†’                chartContainer.classList.remove('visible');
   281â†’                expandIcon.classList.remove('rotated');
   282â†’                row.classList.remove('expanded');
   283â†’            } else {
   284â†’                // Close all other charts
   285â†’                document.querySelectorAll('.chart-container').forEach(c => c.classList.remove('visible'));
   286â†’                document.querySelectorAll('.expand-icon').forEach(i => i.classList.remove('rotated'));
   287â†’                document.querySelectorAll('.signal-row').forEach(r => r.classList.remove('expanded'));
   288â†’
   289â†’                // Open this chart
   290â†’                chartContainer.classList.add('visible');
   291â†’                expandIcon.classList.add('rotated');
   292â†’                row.classList.add('expanded');
   293â†’
   294â†’                // Load chart if not already loaded
   295â†’                loadChart(signalId, chartContainer);
   296â†’            }
   297â†’        }
   298â†’
   299â†’        // Load and render chart
   300â†’        async function loadChart(signalId, container) {
   301â†’            if (container.querySelector('.plotly-graph-div')) {
   302â†’                return; // Already loaded
   303â†’            }
   304â†’
   305â†’            // Show loading state
   306â†’            container.innerHTML = '<div style="text-align: center; padding: 40px; color: #7f8c8d;">Loading chart...</div>';
   307â†’
   308â†’            const signal = window.signalsData.charts[signalId];
   309â†’
   310â†’            try {
   311â†’                // Load detailed chart data (path adjusted for root location)
   312â†’                // Extract filename from data_file (remove "data/" prefix if present)
   313â†’                const filename = signal.data_file.replace(/^data\//, '');
   314â†’                const response = await fetch(`data/charts/data/${filename}`);
   315â†’                const chartData = await response.json();
   316â†’
   317â†’                // Create candlestick chart
   318â†’                const trace = {
   319â†’                    x: chartData.ohlcv.timestamps,
   320â†’                    open: chartData.ohlcv.open,
   321â†’                    high: chartData.ohlcv.high,
   322â†’                    low: chartData.ohlcv.low,
   323â†’                    close: chartData.ohlcv.close,
   324â†’                    type: 'candlestick',
   325â†’                    name: signal.symbol,
   326â†’                    increasing: {line: {color: '#26a69a'}},
   327â†’                    decreasing: {line: {color: '#ef5350'}}
   328â†’                };
   329â†’
   330â†’                // Add entry marker
   331â†’                const entryMarker = {
   332â†’                    x: [chartData.ohlcv.timestamps[chartData.ohlcv.timestamps.length - 1]],
   333â†’                    y: [signal.entry_price],
   334â†’                    mode: 'markers+text',
   335â†’                    marker: {
   336â†’                        symbol: signal.direction === 'long' ? 'triangle-up' : 'triangle-down',
   337â†’                        size: 15,
   338â†’                        color: signal.direction === 'long' ? 'lime' : 'red'
   339â†’                    },
   340â†’                    text: ['ENTRY'],
   341â†’                    textposition: 'top center',
   342â†’                    name: 'Entry Signal'
   343â†’                };
   344â†’
   345â†’                const layout = {
   346â†’                    title: `${signal.symbol} ${signal.timeframe.toUpperCase()} - Score: ${signal.score}/194`,
   347â†’                    height: 500,  // Explicit height to prevent collapse
   348â†’                    autosize: true,  // Enable responsive resizing
   349â†’                    paper_bgcolor: '#1e1e1e',
   350â†’                    plot_bgcolor: '#2c2c2c',
   351â†’                    font: {color: '#ffffff'},
   352â†’                    xaxis: {
   353â†’                        gridcolor: '#3a3a3a',
   354â†’                        rangeslider: {visible: false}
   355â†’                    },
   356â†’                    yaxis: {
   357â†’                        gridcolor: '#3a3a3a'
   358â†’                    },
   359â†’                    shapes: [
   360â†’                        // Stop Loss line
   361â†’                        {
   362â†’                            type: 'line',
   363â†’                            x0: chartData.ohlcv.timestamps[0],
   364â†’                            x1: chartData.ohlcv.timestamps[chartData.ohlcv.timestamps.length - 1],
   365â†’                            y0: signal.stop_loss,
   366â†’                            y1: signal.stop_loss,
   367â†’                            line: {color: 'red', width: 2, dash: 'dash'}
   368â†’                        },
   369â†’                        // Take Profit line
   370â†’                        {
   371â†’                            type: 'line',
   372â†’                            x0: chartData.ohlcv.timestamps[0],
   373â†’                            x1: chartData.ohlcv.timestamps[chartData.ohlcv.timestamps.length - 1],
   374â†’                            y0: signal.take_profit,
   375â†’                            y1: signal.take_profit,
   376â†’                            line: {color: 'green', width: 2, dash: 'dash'}
   377â†’                        }
   378â†’                    ],
   379â†’                    annotations: [
   380â†’                        {
   381â†’                            x: chartData.ohlcv.timestamps[chartData.ohlcv.timestamps.length - 1],
   382â†’                            y: signal.stop_loss,
   383â†’                            text: `SL: ${formatPrice(signal.stop_loss, signal.symbol)}`,
   384â†’                            showarrow: false,
   385â†’                            xanchor: 'left',
   386â†’                            bgcolor: 'rgba(255, 0, 0, 0.3)'
   387â†’                        },
   388â†’                        {
   389â†’                            x: chartData.ohlcv.timestamps[chartData.ohlcv.timestamps.length - 1],
   390â†’                            y: signal.take_profit,
   391â†’                            text: `TP: ${formatPrice(signal.take_profit, signal.symbol)}`,
   392â†’                            showarrow: false,
   393â†’                            xanchor: 'left',
   394â†’                            bgcolor: 'rgba(0, 255, 0, 0.3)'
   395â†’                        }
   396â†’                    ]
   397â†’                };
   398â†’
   399â†’                const config = {
   400â†’                    displayModeBar: true,
   401â†’                    displaylogo: false,
   402â†’                    responsive: true
   403â†’                };
   404â†’
   405â†’                // Clear loading state before rendering
   406â†’                container.innerHTML = '';
   407â†’                Plotly.newPlot(container, [trace, entryMarker], layout, config);
   408â†’
   409â†’            } catch (error) {
   410â†’                container.innerHTML = `
   411â†’                    <div style="color: #e74c3c; padding: 20px; text-align: center;">
   412â†’                        <strong>Error loading chart:</strong><br>
   413â†’                        ${error.message}
   414â†’                        <br><br>
   415â†’                        <button onclick="loadChart(${signalId}, this.closest('.chart-container'))"
   416â†’                                style="padding: 8px 16px; background: #3498db; color: white;
   417â†’                                       border: none; border-radius: 4px; cursor: pointer;">
   418â†’                            Retry
   419â†’                        </button>
   420â†’                    </div>
   421â†’                `;
   422â†’            }
   423â†’        }
   424â†’
   425â†’        // Load signals from index.json
   426â†’        async function loadSignals() {
   427â†’            try {
   428â†’                // Path adjusted for root location
   429â†’                const response = await fetch('data/charts/data/index.json');
   430â†’                const data = await response.json();
   431â†’
   432â†’                window.signalsData = data;
   433â†’
   434â†’                // Update stats
   435â†’                document.getElementById('totalSignals').textContent = data.total_charts;
   436â†’                const longCount = data.charts.filter(s => s.direction === 'long').length;
   437â†’                const shortCount = data.charts.filter(s => s.direction === 'short').length;
   438â†’                document.getElementById('longSignals').textContent = longCount;
   439â†’                document.getElementById('shortSignals').textContent = shortCount;
   440â†’
   441â†’                const avgScore = data.charts.reduce((sum, s) => sum + s.score, 0) / data.total_charts;
   442â†’                document.getElementById('avgScore').textContent = avgScore.toFixed(0);
   443â†’
   444â†’                const avgRR = data.charts.reduce((sum, s) =>
   445â†’                    sum + calculateRR(s.entry_price, s.stop_loss, s.take_profit, s.direction), 0
   446â†’                ) / data.total_charts;
   447â†’                document.getElementById('avgRR').textContent = `1:${avgRR.toFixed(2)}`;
   448â†’
   449â†’                document.getElementById('lastUpdate').textContent =
   450â†’                    `Last Updated: ${formatTimestamp(data.generated_at)}`;
   451â†’
   452â†’                // Build table
   453â†’                let tableHTML = `
   454â†’                    <div class="table-header">
   455â†’                        <div class="col-symbol">Symbol</div>
   456â†’                        <div class="col-direction">Direction</div>
   457â†’                        <div class="col-score">Score</div>
   458â†’                        <div class="col-entry">Entry</div>
   459â†’                        <div class="col-sl">Stop Loss</div>
   460â†’                        <div class="col-tp">Take Profit</div>
   461â†’                        <div class="col-rr">R:R</div>
   462â†’                        <div class="col-timeframe">Timeframe</div>
   463â†’                        <div class="col-timestamp">Detected</div>
   464â†’                        <div class="col-expand"></div>
   465â†’                    </div>
   466â†’                `;
   467â†’
   468â†’                if (data.total_charts === 0) {
   469â†’                    tableHTML += '<div class="no-signals">No signals detected yet</div>';
   470â†’                } else {
   471â†’                    data.charts.forEach((signal, index) => {
   472â†’                        const rr = calculateRR(signal.entry_price, signal.stop_loss, signal.take_profit, signal.direction);
   473â†’                        const scoreClass = getScoreClass(signal.score);
   474â†’
   475â†’                        tableHTML += `
   476â†’                            <div>
   477â†’                                <div class="signal-row" id="row-${index}" onclick="toggleChart(${index})">
   478â†’                                    <div class="col-symbol"><strong>${signal.symbol}</strong></div>
   479â†’                                    <div class="col-direction">
   480â†’                                        <span class="direction-badge direction-${signal.direction}">${signal.direction}</span>
   481â†’                                    </div>
   482â†’                                    <div class="col-score">
   483â†’                                        <span class="${scoreClass}">${signal.score}/194</span>
   484â†’                                    </div>
   485â†’                                    <div class="col-entry price-value">${formatPrice(signal.entry_price, signal.symbol)}</div>
   486â†’                                    <div class="col-sl price-value">${formatPrice(signal.stop_loss, signal.symbol)}</div>
   487â†’                                    <div class="col-tp price-value">${formatPrice(signal.take_profit, signal.symbol)}</div>
   488â†’                                    <div class="col-rr rr-value">1:${rr.toFixed(2)}</div>
   489â†’                                    <div class="col-timeframe">${signal.timeframe.toUpperCase()}</div>
   490â†’                                    <div class="col-timestamp timestamp">${formatTimestamp(signal.timestamp)}</div>
   491â†’                                    <div class="col-expand"><span class="expand-icon" id="icon-${index}">â–¼</span></div>
   492â†’                                </div>
   493â†’                                <div class="chart-container" id="chart-${index}"></div>
   494â†’                            </div>
   495â†’                        `;
   496â†’                    });
   497â†’                }
   498â†’
   499â†’                document.getElementById('signalsContainer').innerHTML = tableHTML;
   500â†’
   501â†’            } catch (error) {
   502â†’                document.getElementById('signalsContainer').innerHTML =
   503â†’                    `<div class="no-signals">Error loading signals: ${error.message}</div>`;
   504â†’            }
   505â†’        }
   506â†’
   507â†’        // Load signals on page load
   508â†’        loadSignals();
   509â†’    </script>
   510â†’</body>
